('.form-inline').length+1) > 2) {				alert("Only 2 control-group allowed");				return false;			}			var id = ($('.form-inline .control-group').length + 1).toString();			$('.form-inline').append('<div class="control-group" id="control-group' + id + '"> \									<div class="spacer" style="height:15px;"></div>\									  <div class="form-group detail-itemname">\										<label class="sr-only" for="itemname">Item' + id + '</label>\									  <input type="text" class="form-control" name="itemname" id="ItemName' + id + '"  placeholder="Name"> \									  </div>\								  	  <div class="form-group detail-more"> \										<button type="button" class="btn btn-default" id="removeButton""><span class="glyphicon glyphicon-comment"></span></button>\									  </div>\									  <div class="form-group detail-itemqty" >\										<label class="sr-only" for="itemnqty' + id + '">Quantity</label>\										<input type="text" class="form-control" name="itemqty" id="ItemQty'+ id + '" placeholder="Pieces">\									  </div>\									  <div class="form-group detail-itemprice" >\										<label class="sr-only" for="itemprice' + id + '" >Unit Price</label>\										<input type="text" class="form-control" name="itemprice" id="ItemPrice' + id + '" placeholder="Amount">\									  </div>  \									  <div class="form-group detail-itemtot" >\										<label class="sr-only" for="itemtotamt' + id + '" >Total</label>\										<input type="text" class="form-control itemtotamt" name="itemtotamt" id="TotalAmt' + id + '" placeholder="Total Amount">\										</div> \										<div class="form-group">\										<button type="button" class="btn btn-danger" id="removeButton" onclick="$(this).parent().parent().remove();;">-</button>\										</div>\									  </div> ');			});	});	</script>	<script>	$(document).ready(function() {		$('input[name=itemqty],input[name=itemprice]').change(function(e) {			var total = 0;			var $row = $(this).parent().parent();			var qty = $row.find('input[name=itemqty]').val();			var price = $row.find('input[name=itemprice]').val();			total = parseFloat(qty * price);			//update the row total			//$row.find('.itemtotamt').text(total);			$row.find('input[name=itemtotamt]').val(total);			var total_amount = 0;						$('input[name=itemtotamt]').each(function() {				//Get the value				//var amt= $(this).text();				var amt = $(this).val();				console.log(amt);				//if it's a number add it to the total				if (IsNumeric(amt)) {					total_amount += parseFloat(amt);				}			});			$("div.content_holder").find('input[name=GrandTotal]').val(total_amount);		});	});	</script>		<script>	//any time the amount changes	$(document).ready(function() {		$("#addButton").click(function () {			$('input[name=itemqty],input[name=itemprice]').change(function(e) {				var total = 0;				var $row = $(this).parent().parent();				var qty = $row.find('input[name=itemqty]').val();				var price = $row.find('input[name=itemprice]').val();				total = parseFloat(qty * price);				//update the row total				//$row.find('.itemtotamt').text(total);				$row.find('input[name=itemtotamt]').val(total);				var total_amount = 0;				$('input[name=itemtotamt]').each(function() {					//Get the value					//var amt= $(this).text();					var amt = $(this).val();					console.log(amt);					//if it's a number add it to the total					if (IsNumeric(amt)) {						total_amount += parseFloat(amt);					}				});				$("div.content_holder").find('input[name=GrandTotal]').val(total_amount);			});		});	});	//isNumeric function Stolen from: 	//http://stackoverflow.com/questions/18082/validate-numbers-in-javascript-isnumeric	function IsNumeric(input) {		return (input - 0) == input && input.length > 0;	}		</script>	  <script type='text/javascript' src='submodal/bootstrap.submodal.js'></script>  </body></html>