                             <div class="form-group detail-more"">\		                                 <a href="#ItemNotes' + id + '" role="button" class="btn btn-default" data-toggle="submodal"><span class="glyphicon glyphicon-tag"></span></a>\									  </div>\									  <div class="form-group detail-itemqty" >\										<label class="sr-only" for="ItemQty' + id + '">Quantity</label>\										<input type="text" name="itemqty" class="form-control" id="ItemQty'+ id + '" placeholder="Pieces">\									  </div>\									  <div class="form-group detail-itemprice" >\										<label class="sr-only" for="ItemPrice' + id + '" >Unit Price</label>\										<input type="text" name="itemprice" class="form-control" id="ItemPrice' + id + '" placeholder="Amount">\									  </div>  \									  <div class="form-group detail-itemtot" >\										<label class="sr-only" for="TotalAmt' + id + '" >Total</label>\										<input type="text" name="itemtotamt" class="form-control" id="TotalAmt' + id + '" placeholder="Total Amount">\										</div> \										<div class="form-group">\										<button type="button" class="btn btn-sm btn-danger" id="removeButton" onclick="$(this).parent().parent().remove();;"><span class="glyphicon glyphicon-trash"></button>\										</div>\									  </div> \									  <div class="modal sub-modal fade" id="ItemNotes' + id + '" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" aria-hidden="true">\										<div class="modal-dialog" style="width:430px;">\											<div class="modal-content">\											<div class="modal-body">\											<form class="form-horizontal">\											<div class="control-group">\											<label class="control-label" for="itemnotes' + id + '" >Notes: </label>\											<hr class="itemnotes" />\											<div class="controls">\												<textarea id="itemnotes' + id + '" class="form-control" rows="4"></textarea>\											</div>\										</div>\									</form>\								</div>\								<div class="modal-footer modal-foot-notes">\									<button class="btn btn-primary" data-dismiss="submodal" aria-hidden="true">Save</button>\								</div>\								</div>\								</div>\							</div>');			});	});	</script>		<script>	//any time the amount changes		$(document).ready(function() {		autoCompute();	});	//autocompute for new lines	$(document).ready(function() {		$("#addButton").click(function () {			autoCompute();		});	});		function autoCompute () {		$('input[name=itemqty],input[name=itemprice]').change(function(e) {			var total = 0;			var $row = $(this).parent().parent();			var qty = $row.find('input[name=itemqty]').val();			var price = $row.find('input[name=itemprice]').val();			total = parseFloat(qty * price);			total = (Math.round(total * 100)/100);			//update the row total			$row.find('input[name=itemtotamt]').val(total);			var total_amount = 0;			$('input[name=itemtotamt]').each(function() {				//Get the value				var amt = $(this).val();				console.log(amt);						//if it's a number add it to the total				if (IsNumeric(amt)) {					total_amount += parseFloat(amt);					total_amount = (Math.round(total_amount * 100)/100);					//total_amount = (total_amount).toFixed(2);					//total_amount = console.log(total_amount.toPrecision(4));				}			});			$("div.content_holder").find('input[name=GrandTotal]').val(total_amount);		});			}	//isNumeric function Stolen from: 	//http://stackoverflow.com/questions/18082/validate-numbers-in-javascript-isnumeric	function IsNumeric(input) {		return (input - 0) == input && input.length > 0;	}			</script>  </body></html>