<?php	$tbl_name="rqsm";	//$qry="SELECT * FROM $tbl_name WHERE $tbl_name.reqid = $requestid";	$qry  = "SELECT $tbl_name.*, vdrm.vid FROM $tbl_name ";	$qry .= "JOIN vdrm ON rqsm.vendor = vdrm.vname ";	$qry .= "WHERE $tbl_name.reqid = $requestid";	$result=mysql_query($qry);	if($result) {		$_SESSION['RQSM_ROWS'] = mysql_num_rows($result);		$rqdetail = mysql_fetch_assoc($result);		$_SESSION['RQSM_REQID'] 	= $rqdetail['reqid'];		$_SESSION['RQSM_POID'] 		= $rqdetail['po_id'];		$_SESSION['RQSM_DATE'] 		= $rqdetail['date'];		$_SESSION['RQSM_ITEM'] 		= $rqdetail['item'];		$_SESSION['RQSM_RQSTOR']	= $rqdetail['requestor'];		$_SESSION['RQSM_RQSTOR_NAME']	= $rqdetail['requestor_name'];		$_SESSION['RQSM_AMOUNT']	= $rqdetail['amount'];		$_SESSION['RQSM_NOTES']		= $rqdetail['notes'];		$_SESSION['RQSM_VENDOR']	= $rqdetail['vendor'];		$_SESSION['RQSM_VENDTIN']	= $rqdetail['vendor_tin'];		$_SESSION['RQSM_VENDTRMS']	= $rqdetail['vendor_terms'];		$_SESSION['RQSM_VENDADDR']	= $rqdetail['vendor_address'];		$_SESSION['RQSM_VENDPRSN']	= $rqdetail['vendor_contact'];					$_SESSION['RQSM_VENDNUM']	= $rqdetail['vendor_contactnum'];		$_SESSION['RQSM_VENDID']	= $rqdetail['vid'];		$_SESSION['SESS_QRY']		= $qry;					switch ($rqdetail['status']) {			case 0:				$_SESSION['RQSM_STAT'] = "New";				$_SESSION['RQSM_STAT_VAL'] = "0";				break;			case 1:				$_SESSION['RQSM_STAT'] = "Done";				$_SESSION['RQSM_STAT_VAL'] = "1";				$_SESSION['RQSM_STAT_X1'] = "Disapproved";				$_SESSION['RQSM_STAT_X1VAL'] = "8";					$_SESSION['RQSM_STAT_X2'] = "Cancelled";				$_SESSION['RQSM_STAT_X2VAL'] = "9";												break;			case 2:				$_SESSION['RQSM_STAT'] = "For Approval";				break;			case 3:				$_SESSION['RQSM_STAT'] = "Accounting";				break;			case 4:				$_SESSION['RQSM_STAT'] = "Delivery/Pickup";				break;			case 5:				$_SESSION['RQSM_STAT'] = "Finished";				break;			case 8:				$_SESSION['RQSM_STAT'] = "Disapproved";				$_SESSION['RQSM_STAT_VAL'] = "8";				$_SESSION['RQSM_STAT_X1'] = "Done";				$_SESSION['RQSM_STAT_X1VAL'] = "1";					$_SESSION['RQSM_STAT_X2'] = "Cancelled";				$_SESSION['RQSM_STAT_X2VAL'] = "9";									break;							case 9:				$_SESSION['RQSM_STAT'] = "Cancelled";				$_SESSION['RQSM_STAT_VAL'] = "9";				$_SESSION['RQSM_STAT_X1'] = "Done";				$_SESSION['RQSM_STAT_X1VAL'] = "1";					$_SESSION['RQSM_STAT_X2'] = "Disapproved";				$_SESSION['RQSM_STAT_X2VAL'] = "8";										break;			default:				$_SESSION['RQSM_STAT'] = "New";		}	}else {		$logmessage = "Query failed!!! $result $qry $tbl_name";		createLogfile($logmessage, 12);	};//fetch the request details	$tbl_name="rqsd";	$current_po_id = $_SESSION['RQSM_POID'];	$current_reqid = $_SESSION['RQSM_REQID'];	//$qry="SELECT * FROM $tbl_name WHERE $tbl_name.po_id = '$current_po_id'";	$qry="SELECT * FROM $tbl_name WHERE $tbl_name.reqid = $current_reqid";	$result=mysql_query($qry);	if($result) {		$_SESSION['RQSD_ROWS'] = mysql_num_rows($result);		$_SESSION['SESS_QRY'] = $qry;	}else {		$logmessage = "Query failed!!! $result $qry $tbl_name";		createLogfile($logmessage, 12);	};			?>