<?php	$tbl_name="usrm";		$qry="SELECT * FROM $tbl_name WHERE $tbl_name.id = $userid";	$result=mysql_query($qry);	$member = mysql_fetch_assoc($result);	if($member) {		$_SESSION['MEMBER_USRID']   = $member['id'];					$_SESSION['MEMBER_USRNAME'] = $member['username'];					$_SESSION['MEMBER_FNAME']   = $member['firstname'];		$_SESSION['MEMBER_LNAME']   = $member['lastname'];		$_SESSION['MEMBER_EMAIL']   = $member['emailadd'];		$_SESSION['MEMBER_DEPT']    = $member['empdepartment'];		$_SESSION['MEMBER_POST']    = $member['empposition'];		$_SESSION['MEMBER_USRFLG']  = $member['activeflg'];		$_SESSION['MEMBER_ADMFLG']  = $member['adminflg'];		$_SESSION['LOGGEDIN'] 		= $member['username'];		switch ($member['adminflg']) {			case "Y":				$_SESSION['MEMBER_FLG'] = "Admin";				$_SESSION['MEMBER_ADMFLG'] = "Y";				$_SESSION['MEMBER_FLG_X'] = "Non-Admin";				$_SESSION['MEMBER_ADMFLG_X'] = "N";										break;			case "N":				$_SESSION['MEMBER_FLG_X'] = "Admin";				$_SESSION['MEMBER_ADMFLG_X'] = "Y";					$_SESSION['MEMBER_FLG'] = "Non-Admin";				$_SESSION['MEMBER_ADMFLG'] = "N";									break;			}			} else {		$logmessage = "Query failed!!! $result $qry $tbl_name";		createLogfile($logmessage, 12);	};	$_SESSION['MEMBER_QRY']  = $qry;		?>