<?php	$tbl_name="vdrm";	$qry="SELECT * FROM $tbl_name WHERE $tbl_name.vid = $vendor_id";	$result=mysql_query($qry);	if($result) {		$_SESSION['VDRM_ROWS'] = mysql_num_rows($result);		$vdr_detail = mysql_fetch_assoc($result);		$_SESSION['VDRM_ID'] 	= $vdr_detail['vid'];		$_SESSION['VDRM_NAME'] 		= $vdr_detail['vname'];		$_SESSION['VDRM_ADDRESS'] 	= $vdr_detail['vaddress'];		$_SESSION['VDRM_PERSON'] 		= $vdr_detail['vperson'];		$_SESSION['VDRM_NUMBR']	= $vdr_detail['vcontact'];		$_SESSION['VDRM_TIN']	= $vdr_detail['vtin'];		$_SESSION['VDRM_PAYMODE']		= $vdr_detail['vmode'];		$_SESSION['VDRM_LASTTXN']	= $vdr_detail['last_tran'];		$_SESSION['VDRM_BUSS_TYPE']	= $vdr_detail['buss_type'];		 $_SESSION['VDRM_NOTES']	= $vdr_detail['notes'];		$_SESSION['SESS_QRY']		= $qry;				}else {		$logmessage = "Query failed!!! $result $qry";		createLogfile($logmessage, 12);				};?>